            <h1 class="font-semibold text-2xl mb-2">Sign Up</h1>
            <p class="text-xs mb-10">Please enter your details</p>

            <form id="registerform">
                <div class="grid gap-4 mb-2">
                    <div class="grid input">
                        <label for="username" class="text-left text-xs font-semibold mb-1">Username</label>
                        <input type="text" name="username" id="username" class="border-b-2 outline-none py-1 px-2">
                        <p id="userError" class="text-xs text-left text-red-500 mt-1 hidden">Username is empty</p>
                    </div>
                    <div class="grid input">
                        <label for="password" class="text-left text-xs font-semibold mb-1">Password</label>
                        <input type="password" name="password" id="password" class="border-b-2 outline-none py-1 px-2">
                        <p id="passError" class="text-xs text-left text-red-500 mt-1 hidden">Password is empty</p>
                    </div>
                </div>

                <button type="submit"
                    class="w-full h-11 my-8 bg-gray-900 rounded-full text-white font-medium hover:bg-slate-800">Sign
                    Up</button>
                <p class="text-xs mb-8">Already have an account? <a href="." class="font-semibold">Sign In</a></p>
            </form>
        </section>
